import { getArticleList } from '@src/services/article';
import { RESET_ARTICLE_LIST, UPD_ARTICLE_LIST } from './types';
//actionCreator
export function updArticle(articleList) {
  return {
    type: UPD_ARTICLE_LIST,
    payload: {
      articleList
    }
  };
} // 清除文章列表

export function resetArticle() {
  return {
    type: RESET_ARTICLE_LIST
  };
} // action

export const thunkUpdArticle = params => async dispatch => {
  const {
    data
  } = await getArticleList(params);
  dispatch(updArticle(data.list));
  return data;
};
