import { getTagList } from '@src/services/tag';
import { RESET_TAG_LIST, UPD_TAG_LIST } from './types';
//actionCreator
export function updTag(tagList) {
  return {
    type: UPD_TAG_LIST,
    payload: {
      tagList
    }
  };
}
export function resetTag() {
  return {
    type: RESET_TAG_LIST
  };
} // action

export const thunkUpdTag = () => async (dispatch, getState) => {
  const {
    tag: {
      tagList,
      tagMap
    }
  } = getState();

  if (tagList && tagList.length) {
    return Promise.resolve(Object.values(tagMap));
  }

  const {
    data
  } = await getTagList();
  dispatch(updTag(data));
  return data;
};
